val data = labelsAndData.values.cache()
val kmeans = new KMeans()
val model = kmeans.setSeed(0).setRuns(10).run(data) 
model.clusterCenters.foreach(println)

val clusterLabelCount = labelsAndData.map {case (label,datum) => 
    val cluster = model.predict(datum)
    (cluster,label)
}.countByValue


// Sort by the number of occurrences of the label
clusterLabelCount.toSeq.sortWith(_._2 > _._2).foreach { 
    case ((cluster,label),count) =>
    println(f"$cluster%1s$label%18s$count%8s")
}

def distance(a: Vector, b: Vector): Double = math.sqrt(a.toArray.zip(b.toArray).map(p => p._1 - p._2).map(d => d * d).sum)

def distToCentroid(datum: Vector, model: KMeansModel):Double = {
    val cluster = model.predict(datum)
    val centroid = model.clusterCenters(cluster) 
    distance(centroid, datum)
}

// Try range a of cluster sizes
// It is not necessary for K-means to return the optimal solution,
// The convergence starts from a random point to a local minimum which may or may not be the global minimum.
(10 to 100 by 10).map(k => (k, clusteringScore(data, k))).
    foreach(println)

def save(inputDf:DataFrame, outDirPath:String): Unit = {
    // Persist the table to disk for future use
    inputDf.repartition(1).write.mode("append").
                    format("com.databricks.spark.csv").option("header", "true").
                    save(outDirPath)
}

